        public static List<Answer> RandomAnswers(int id)
        {
            List<Answer> tempList= new();

            Random rnd = new();

            for (int i = 0; i < 2; i++)
            {
                int tempint = rnd.Next(1, 57);
                Answer temp = AnswerList.Where(x => x.Id == tempint).FirstOrDefault();
                tempList.Add(temp);
            }
            Answer temp2 = AnswerList.Where(x => x.Id == id).FirstOrDefault();
            tempList.Add(temp2);

            return tempList;
        }
